import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import * as Font from 'expo-font';
import {AppLoading} from 'expo';
import { StyleSheet, Text, View , Image, TextInput , TouchableOpacity} from 'react-native';
import { useHistory } from 'react-router-native';

const getFonts = () => Font.loadAsync({
  'sansation-regular': require('../../assets/fonts/Sansation_Regular.ttf')
});

export default function Login() {

    const [email, setEmail] = useState(''); //Como se fosse {get; set;}
    const [senha, setSenha] = useState(''); // acessar e alterar //get set

    const login = () => { //Arrow function login
        const login = { //váriavel login para armazenar um array {}
            email: email,
            senha: senha   //Acessando e alterando (modelo) os dados como se fosse no postman
        }

        fetch('http://wolfrosfay4-001-site1.btempurl.com/api/login', { //Fetch passando a url da API
            method: 'POST', //Como é um login o método é o Post
            body: JSON.stringify(login), //Seleciona o tipo do corpo, no caso é json (Postman: Raw/Json)
            headers: {
                'content-type': 'application/json' //Tipo do conteudo é uma aplicação Json
            }
        })
            .then(response => response.json())
            .then(dados => {
                if (dados.token != undefined) {  //Se dados token for diferente de Undefined
                    localStorage.setItem('token-filmes', dados.token) //Local storage irá adicionar esta chave ao storage, ou atualizar o valor caso a chave já exista
                } else
                    alert('Email ou senha inválidos') //Alerta caso o email ou senha estejam errados
                })
                
            .catch(error => console.error(error)) // Como usando o fetch colocamos um catch caso algo de errado.
    }
  const [fontsLoaded, setFontsLoaded] = useState(false);

  if (fontsLoaded) {
    return (
      <View style={styles.container}>
     
      <Image 
       style={styles.logo}
       source={require('../../assets/images/LogoAppVermelho.png')}
       />
 
      <Text style={styles.tlogin}>Faça seu login</Text>
     
       <TextInput
       style={styles.input}
       placeholder=""
       value={email}
          onChangeText={email => setEmail(email)}
       />
       
       <TextInput
       style={styles.input}
       secureTextEntry={true}
       placeholder=""
       value={senha}
          onChangeText={senha => setSenha(senha)}
       />
 
       <TouchableOpacity onPress={login} style={styles.button}>
         <Text>
           Login
         </Text>
       </TouchableOpacity>
 
       <TouchableOpacity style={styles.buttone}>
         <Text>
           Esqueci minha senha
         </Text>
       </TouchableOpacity>
 
       <TouchableOpacity style={styles.buttonc}>
         <Text>
           cadastrar como candidato
         </Text>
       </TouchableOpacity>
 
       <TouchableOpacity style={styles.buttonc}>
         <Text>
           cadastrar como empresa
         </Text>
       </TouchableOpacity>
 
     </View>
   );
 
    
  } else {
    return (
      <AppLoading 
        startAsync={getFonts} 
        onFinish={() => setFontsLoaded(true)} 
      />
    )
  }
  
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FBFBFB',
    alignItems: 'center',
    justifyContent: 'center',
  },
  
  logo: {
    width:250,
    height: 90,
    //top: -150,
  },

  input: {
    backgroundColor: '#FFFFFF',
    margin: 10,
    borderRadius: 15,
    width: 250,
    height:35,
    shadowColor: "black",
  },

  button: {
    backgroundColor: '#D20003',
    margin: 10,
    borderRadius: 15,
    width: 250,
    height:35
  },

  buttone: {
    backgroundColor: '#016799',
    margin: 10,
    borderRadius: 15,
    width: 250,
    height:35
  },

  buttonc: {
    backgroundColor: '#ffff',
    margin: 10,
    borderRadius: 15,
    width: 200,
    height:35,
    borderColor: '#016799',
    borderWidth: 3,
    textDecorationColor: '#016799'
  },

  tlogin: {
    textDecorationColor: '#505050',
    fontFamily: 'sansation-regular'
    
    
  }
});
