import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import * as Font from 'expo-font';
import {AppLoading} from 'expo';
import { StyleSheet, Text, View , Image, TextInput , TouchableOpacity} from 'react-native';
import { useHistory } from 'react-router-native';

const getFonts = () => Font.loadAsync({
  'sansation-regular': require('../../assets/fonts/Sansation_Regular.ttf')
});

export default function Login() {

    const [email, setEmail] = useState(''); //Como se fosse {get; set;}
    const [senha, setSenha] = useState(''); // acessar e alterar //get set

    const login = () => { //Arrow function login
        const login = { //v√°riavel login para armazenar um array {}
            email: email,
            senha: senha   //Acessando e alterando (modelo) os dados como se fosse no postman
        }

        fetch('http://wolfrosfay4-001-site1.btempurl.com/api/login', { //Fetch passando a url da API
            method: 'POST', //Como √© um login o m√©todo √© o Post
            body: JSON.stringify(login), //Seleciona o tipo do corpo, no caso √© json (Postman: Raw/Json)
            headers: {
                'content-type': 'application/json' //Tipo do conteudo √© uma aplica√ß√£o Json
            }
        })
            .then(response => response.json())
            .then(dados => {
                if (dados.token != undefined) {  //Se dados token for diferente de Undefined
                    localStorage.setItem('token-filmes', dados.token) //Local storage ir√° adicionar esta chave ao storage, ou atualizar o valor caso a chave j√° exista
                } else
                    alert('Email ou senha inv√°lidos') //Alerta caso o email ou senha estejam errados
                })
                
            .catch(error => console.error(error)) // Como usando o fetch colocamos um catch caso algo de errado.
    }
  const [fontsLoaded, setFontsLoaded] = useState(false);


    if (fontsLoaded) {
      return (
        <View style={styles.container}>
       <View>
       <Image 
         style={styles.logo}
         source={require('../../assets/images/LogoAppVermelho.png')}
         />
       </View>
        
        <Text style={styles.tlogin}>Fa√ßa seu login</Text>
        
        <Text style={styles.ti}>Email</Text>
         <TextInput
         style={styles.input}
         placeholder=""
         />
  
         <Text style={styles.ti}>Senha</Text>
         <TextInput
         style={styles.input}
         secureTextEntry={true}
         placeholder=""
         />
   
         <TouchableOpacity style={styles.button}>
           <Text style={styles.tloginnn}>
             Login
           </Text>
         </TouchableOpacity>
   
         <TouchableOpacity style={styles.buttone}>
           <Text style={styles.tloginnn}>
             Esqueci minha senha
           </Text>
         </TouchableOpacity>
   
         <TouchableOpacity style={styles.buttonc}>
           <Text style={styles.tloginn}>
             cadastrar como candidato
           </Text>
         </TouchableOpacity>
   
         <TouchableOpacity style={styles.buttonc}>
           <Text style={styles.tloginn}>
             cadastrar como empresa
           </Text>
         </TouchableOpacity>
   
       </View>
     );
   
      
    } else {
      return (
        <AppLoading 
          startAsync={getFonts} 
          onFinish={() => setFontsLoaded(true)} 
        />
      )
    }
    
  }
  
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#FBFBFB',
      alignItems: 'center',
      justifyContent: 'center',
      height: 860,
      
    },
    
    logo: {
      width:220,
      height: 60,
     
    },
  
    input: {
      backgroundColor: '#FFFFFF',
      margin: 10,
      borderRadius: 15,
      width: 200,
      height:35,
      shadowOffset:{  width: 2,  height: 2,  },
      shadowColor: 'black',
      shadowOpacity: 0.3,
    },
  
    button: {
      backgroundColor: '#D20003',
      margin: 10,
      borderRadius: 15,
      width: 200,
      height:35,
      textAlign: "center",
      justifyContent: 'center',
      
    },
  
    buttone: {
      backgroundColor: '#016799',
      margin: 10,
      borderRadius: 15,
      width: 200,
      height:35,
      textAlign: "center",
      justifyContent: 'center',
  
    },
  
    buttonc: {
      backgroundColor: '#ffff',
      margin: 10,
      borderRadius: 15,
      width: 150,
      height:35,
      textAlign: "center",
      justifyContent: 'center',
      shadowOffset:{  width: 2,  height: 2,  },
      shadowColor: 'black',
      shadowOpacity: 0.3,
      fontSize: 12
      
  
    },
  
    tlogin: {
      textDecorationColor: '#505050',
      fontFamily: 'sansation-regular'
    },
  
    ti:{
      marginRight: 150,
      textDecorationColor: '#505050',
      fontFamily: 'sansation-regular'
    },
  
    tloginn: {
      color: '#016799',
      fontFamily: 'sansation-regular'
    },
    tloginnn:{
      fontFamily: 'sansation-regular',
      color:'#E0E0E0'
    }
  });
