import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import * as Font from 'expo-font';
import {AppLoading} from 'expo';
import { StyleSheet, Text, View , Image, TextInput , TouchableOpacity} from 'react-native';
import api from '../services/api';

const getFonts = () => Font.loadAsync({
  'sansation-regular': require('../assets/fonts/Sansation_Regular.ttf')
});



export default function Login() {
 
  const [fontsLoaded, setFontsLoaded] = useState(false);

  if (fontsLoaded) {
    return (
      <View style={styles.container}>
     <View>
     <Image 
       style={styles.logo}
       source={require('../assets/images/LogoAppVermelho.png')}
       />
     </View>
      
      <Text style={styles.tlogin}>Faça seu login</Text>
      
      <Text style={styles.ti}>Email</Text>
       <TextInput
       style={styles.input}
       placeholder=""
       />

       <Text style={styles.ti}>Senha</Text>
       <TextInput
       style={styles.input}
       secureTextEntry={true}
       placeholder=""
       />
 
       <TouchableOpacity style={styles.button} >
         <Text style={styles.tloginnn}>
           Login
         </Text>
       </TouchableOpacity>
 
       <TouchableOpacity style={styles.buttone}>
         <Text style={styles.tloginnn}>
           Esqueci minha senha
         </Text>
       </TouchableOpacity>
 
       <TouchableOpacity style={styles.buttonc}>
         <Text style={styles.tloginn}>
           cadastrar como candidato
         </Text>
       </TouchableOpacity>
 
       <TouchableOpacity style={styles.buttonc}>
         <Text style={styles.tloginn}>
           cadastrar como empresa
         </Text>
       </TouchableOpacity>
 
     </View>
   );
 
    
  } else {
    return (
      <AppLoading 
        startAsync={getFonts} 
        onFinish={() => setFontsLoaded(true)} 
      />
    )
  }
  
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FBFBFB',
    alignItems: 'center',
    justifyContent: 'center',
    
  },
  
  logo: {
    width:220,
    height: 60,
    margin: 40
   
  },

  input: {
    backgroundColor: '#FFFFFF',
    margin: 10,
    borderRadius: 15,
    width: 250,
    height:35,
    shadowOffset:{  width: 2,  height: 2,  },
    shadowColor: 'black',
    shadowOpacity: 0.3,
  },

  button: {
    backgroundColor: '#D20003',
    margin: 10,
    borderRadius: 15,
    width: 250,
    height:35,
    textAlign: "center",
    justifyContent: 'center',
    
  },

  buttone: {
    backgroundColor: '#016799',
    margin: 10,
    borderRadius: 15,
    width: 250,
    height:35,
    textAlign: "center",
    justifyContent: 'center',

  },

  buttonc: {
    backgroundColor: '#ffff',
    margin: 10,
    borderRadius: 15,
    width: 200,
    height:35,
    textAlign: "center",
    justifyContent: 'center',
    shadowOffset:{  width: 2,  height: 2,  },
    shadowColor: 'black',
    shadowOpacity: 0.3,
    fontSize: 12
    

  },

  tlogin: {
    textDecorationColor: '#505050',
    fontFamily: 'sansation-regular',
    margin: 30,
  },

  ti:{
    marginRight: 190,
    textDecorationColor: '#505050',
    fontFamily: 'sansation-regular'
  },

  tloginn: {
    color: '#016799',
    fontFamily: 'sansation-regular',
    textAlign: "center",
    justifyContent: 'center',
  },
  tloginnn:{
    fontFamily: 'sansation-regular',
    color:'#E0E0E0',
    textAlign: "center",
    justifyContent: 'center',
  }
});
