import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import * as Font from 'expo-font';
import {AppLoading} from 'expo';
import { StyleSheet, Text, View , Image, TextInput , TouchableOpacity} from 'react-native';

const getFonts = () => Font.loadAsync({
  'sansation-regular': require('../assets/fonts/Sansation_Regular.ttf')
});

export default function Login() {
  const [fontsLoaded, setFontsLoaded] = useState(false);

  if (fontsLoaded) {
    return (
      <Login />
    );
  } else {
    return (
      <AppLoading 
        startAsync={getFonts} 
        onFinish={() => setFontsLoaded(true)} 
      />
    )
  }
  return (
    <View style={styles.container}>
     
     <Image 
      style={styles.logo}
      source={require('../assets/images/LogoAppVermelho.png')}
      />

     <Text style={styles.tlogin}>Fa√ßa seu login</Text>
    
      <TextInput
      style={styles.input}
      placeholder=""
      />
      
      <TextInput
      style={styles.input}
      secureTextEntry={true}
      placeholder=""
      />

      <TouchableOpacity style={styles.button}>
        <Text>
          Login
        </Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.buttone}>
        <Text>
          Esqueci minha senha
        </Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.buttonc}>
        <Text>
          cadastrar como candidato
        </Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.buttonc}>
        <Text>
          cadastrar como empresa
        </Text>
      </TouchableOpacity>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FBFBFB',
    alignItems: 'center',
    justifyContent: 'center',
  },
  
  logo: {
    width:250,
    height: 90,
    //top: -150,
  },

  input: {
    backgroundColor: '#FFFFFF',
    margin: 10,
    borderRadius: 15,
    width: 250,
    height:35,
    shadowColor: "black",
  },

  button: {
    backgroundColor: '#D20003',
    margin: 10,
    borderRadius: 15,
    width: 250,
    height:35
  },

  buttone: {
    backgroundColor: '#016799',
    margin: 10,
    borderRadius: 15,
    width: 250,
    height:35
  },

  buttonc: {
    backgroundColor: '#ffff',
    margin: 10,
    borderRadius: 15,
    width: 200,
    height:35,
    borderColor: '#016799',
    borderWidth: 3,
    textDecorationColor: '#016799'
  },

  tlogin: {
    textDecorationColor: '#505050',
    fontFamily: 'sansation-regular'
    
    
  }
});
