import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import * as Font from 'expo-font';
import {AppLoading} from 'expo';
import { StyleSheet, Text, View , Image, TextInput , TouchableOpacity, Alert} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

import { useHistory } from 'react-router-native';

const getFonts = () => Font.loadAsync({
  'sansation-regular': require('../../assets/fonts/Sansation_Regular.ttf')
});

export default function Login() {

  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  
  const login = () => {
      const form = {
          email: email,
          senha: senha,
      };

      if (form.email.length === 0 || form.senha.length === 0) {
          return Alert.alert('Preencha todos os campos.');
      }

      const init = {
          method: 'post',
          headers: {'Content-Type': 'application/json',},
          body: JSON.stringify(form),
      };

      fetch('http://wolfrosfay4-001-site1.btempurl.com/api/login', init)
          .then(resp => resp.json())
          .then(data => {
              if (data.token !== undefined) {
                  AsyncStorage.setItem('token-usuario', data.token)
                  console.log('token: ' + data.token)
              }
              else {
                Alert.alert(
                  "OOPS!!",
                  "vcrifique os dados inseridos",
                  [
                    {
                      text: "OK",
                      onPress: () => console.log("Cancel Pressed"),  
                    },
                  ],
                  { cancelable: false }
                );
            
              }
          })
          .catch(erro => console.log(erro));
  };
  const [fontsLoaded, setFontsLoaded] = useState(false);


    if (fontsLoaded) {
      return (
        <View style={styles.container}>
       <View>
       <Image 
         style={styles.logo}
         source={require('../../assets/images/LogoAppVermelho.png')}
         />
       </View>
        
        <Text style={styles.tlogin}>Fa√ßa seu login</Text>
        
        <Text style={styles.ti}>Email</Text>
         <TextInput
         style={styles.input}
         placeholder=""
         onChangeText={(e: any) => setEmail(e)}
         />
  
         <Text style={styles.ti}>Senha</Text>
         <TextInput
         style={styles.input}
         secureTextEntry={true}
         placeholder=""
         onChangeText={(e: any) => setSenha(e)}
         />
   
         <TouchableOpacity  onPress={event => {
                        event.preventDefault();
                        login();
                    }} style={styles.button}>
           <Text style={styles.tloginnn}>
             Login
           </Text>
         </TouchableOpacity>
   
         <TouchableOpacity style={styles.buttone}>
           <Text style={styles.tloginnn}>
             Esqueci minha senha
           </Text>
         </TouchableOpacity>
   
         <TouchableOpacity style={styles.buttonc}>
           <Text style={styles.tloginn}>
             cadastrar como candidato
           </Text>
         </TouchableOpacity>
   
         <TouchableOpacity style={styles.buttonc}>
           <Text style={styles.tloginn}>
             cadastrar como empresa
           </Text>
         </TouchableOpacity>
   
       </View>
     );
   
      
    } else {
      return (
        <AppLoading 
          startAsync={getFonts} 
          onFinish={() => setFontsLoaded(true)} 
        />
      )
    }
    
  }
  
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#FBFBFB',
      alignItems: 'center',
      justifyContent: 'center',
      height: 860,
      
    },
    
    logo: {
      width:220,
      height: 60,
     
    },
  
    input: {
      backgroundColor: '#FFFFFF',
      margin: 10,
      borderRadius: 15,
      width: 200,
      height:35,
      //shadowOffset:{  width: 2,  height: 2,  },
      //shadowColor: 'black',
      //shadowOpacity: 0.3,
      borderColor: "black",
      borderWidth: 0.2,
    },
  
    button: {
      backgroundColor: '#D20003',
      margin: 10,
      borderRadius: 15,
      width: 200,
      height:35,
      textAlign: "center",
      justifyContent: 'center',
      
    },
  
    buttone: {
      backgroundColor: '#016799',
      margin: 10,
      borderRadius: 15,
      width: 200,
      height:35,
      textAlign: "center",
      justifyContent: 'center',
  
    },
  
    buttonc: {
      backgroundColor: '#ffff',
      margin: 10,
      borderRadius: 15,
      width: 150,
      height:35,
      textAlign: "center",
      justifyContent: 'center',
      shadowOffset:{  width: 2,  height: 2,  },
      shadowColor: 'black',
      shadowOpacity: 0.3,
      fontSize: 12
      
  
    },
  
    tlogin: {
      textDecorationColor: '#505050',
      fontFamily: 'sansation-regular'
    },
  
    ti:{
      marginRight: 150,
      textDecorationColor: '#505050',
      fontFamily: 'sansation-regular'
    },
  
    tloginn: {
      color: '#016799',
      fontFamily: 'sansation-regular',
      textAlign: "center",
      justifyContent: 'center',
    },
    tloginnn:{
      fontFamily: 'sansation-regular',
      color:'#E0E0E0',
      textAlign: "center",
      justifyContent: 'center',
    }
  });
